<?php

namespace appbox\voyageBundle\Repository;

/**
 * reviewRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class reviewRepository extends \Doctrine\ORM\EntityRepository
{
    public function reviewforvoyage($id)
    {
        $qb = $this->createQueryBuilder('r');
        $qb->where('r.voyage = :id')
            ->setParameter('id',$id)
            ->andwhere('r.valider = :ide')
            ->setParameter('ide',true)
            ->orderBy('r.date', 'desc');
        return $qb->getQuery()->getResult();

    }
    public function tousreview()
    {
        $qb = $this->createQueryBuilder('r');
        $qb->where('r.valider = :id')
            ->setParameter('id',true)
            ->orderBy('r.date', 'desc');
        return $qb->getQuery()->getResult();

    }
    
}
